mapper.select:
select * from product where 1=1

	and theme_id = #{theme.theme_id}


	and player_range_id = #{playerRange.player_range_id}


	and age_range_id = #{ageRange.age_range_id}


	and difficulty_id = #{difficulty.difficulty_id}

mapper.insert:
insert into product(
	product_name, price, discount_rate, product_quantity, description, regdate,
	play_time, image, theme_id, player_range_id, age_range_id, difficulty_id)
values(#{product_name}, #{price}, #{discount_rate}, #{product_quantity}, #{description}, #{regdate},
	#{play_time}, #{image}, #{theme.theme_id}, #{playerRange.player_range_id}, #{ageRange.age_range_id}, #{difficulty.difficulty_id}

)
